mapper.insert:
insert into
	item_db(
		item_num
		,acc_id
		,acc_name
		,item_option
		,item_title
		,item_contents
		,item_price
		,item_fileNum
	)values(
		item_db_seq.nextval
		,#{acc_id}
		,#{acc_name}
		,#{item_option}
		,#{item_title}
		,#{item_contents}
		,#{item_price}
		,#{item_fileNum}
	)

		select item_db_seq.currval
		from dual

insert into
	com_db(
		comment_no
		,acc_id
		,acc_name
		,item_num
		,comment_contents
	)values(
		comment_db_seq.nextval
		,#{acc_id}
		,#{acc_name}
		,#{item_num}
		,#{comment_contents}
	)

			select comment_db_seq.currval
			from dual
